        <script>
            document.addEventListener("DOMContentLoaded", function () {
                // Get all section IDs from the navbar links
                const sections = document.querySelectorAll("section");
                const navLinks = document.querySelectorAll(".tour_Details_navbar ul li a");
          
                // Observe which section is in view
                const observer = new IntersectionObserver(entries => {
                    entries.forEach(entry => {
                        if (entry.isIntersecting) {
                            let activeId = entry.target.getAttribute("id");
          
                            // Remove active class from all links
                            navLinks.forEach(link => {
                                link.classList.remove("activated_item");
                                link.style.color = "rgb(95,95,95)"; // Reset color
                                link.style.borderBottom = "none"; // Reset border
                            });
          
                            // Find and activate the corresponding nav link
                            let activeLink = document.querySelector(`.tour_Details_navbar ul li a[href="#${activeId}"]`);
                            if (activeLink) {
                                activeLink.classList.add("activated_item");
                                activeLink.style.color = "#008cff"; // Highlight active link
                                activeLink.style.borderBottom = "3px solid #008cff"; // Underline active link
                            }
                        }
                    });
                }, { threshold: 0.5 }); // Trigger when 50% of the section is visible
          
                // Attach observer to all sections
                sections.forEach(section => observer.observe(section));
            });
          </script>
          
