<script>
    function incrementAdults() {
        const numPersonsInput = document.getElementById('numPersons');
        numPersonsInput.value = parseInt(numPersonsInput.value) + 1;
    }

    function decrementAdults() {
        const numPersonsInput = document.getElementById('numPersons');
        const currentValue = parseInt(numPersonsInput.value);
        if (currentValue > 1) {
            numPersonsInput.value = currentValue - 1;
        }
    }


    function setCurrentDate() {
        const travelDateInput = document.getElementById('travelDate');

        // Get the current date
        const today = new Date();
        const year = today.getFullYear();
        const month = String(today.getMonth() + 1).padStart(2, '0'); // Months are zero-based
        const day = String(today.getDate()).padStart(2, '0');

        // Set the value of the input to the current date
        travelDateInput.value = `${year}-${month}-${day}`;
    }

    // Call the function when the page loads
    window.onload = setCurrentDate;
    const input = document.getElementById('pickupLocation');
    const suggestionsBox = document.getElementById('suggestions');
    let debounceTimeout;
    let cache = {};  // Cache to store previous search results

    // Function to handle the fetch request and show suggestions
    async function fetchSuggestions() {
        const query = input.value.trim();
        if (query.length < 3) {
            suggestionsBox.innerHTML = '';  // Clear suggestions if the query is too short
            return;
        }

        // Check if the query has been cached
        if (cache[query]) {
            displaySuggestions(cache[query]);
            return;
        }

        // Define Kolkata bounding box (SW and NE corners)
        const viewbox = '88.3054,22.4569,88.4254,22.6769';  // Longitude,Latitude for SW and NE corners of Kolkata
        const bounded = 1;  // Restrict results within the bounding box

        try {
            const response = await fetch(`https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(query)}&format=json&addressdetails=1&limit=5&viewbox=${viewbox}&bounded=${bounded}`);
            const results = await response.json();

            // Cache the results for future use
            cache[query] = results;

            // Display suggestions
            displaySuggestions(results);
        } catch (error) {
            console.error('Error fetching pickupLocation suggestions:', error);
        }
    }

    // Function to display the suggestions in the suggestions box
    function displaySuggestions(results) {
        suggestionsBox.innerHTML = ''; // Clear previous suggestions
        results.forEach(result => {
            const div = document.createElement('div');
            div.textContent = result.display_name;
            div.addEventListener('click', () => {
                input.value = result.display_name;
                suggestionsBox.innerHTML = '';  // Clear suggestions after selection
            });
            suggestionsBox.appendChild(div);
        });
    }

    // Add event listener to handle the user input and trigger debounce
    input.addEventListener('input', () => {
        clearTimeout(debounceTimeout);  // Clear the previous timeout if the user continues typing
        debounceTimeout = setTimeout(fetchSuggestions, 200);  // Delay the API call for 200ms after the user stops typing
    });

    // Close suggestions if the user clicks outside the suggestions box or the input field
    document.addEventListener('click', (e) => {
        if (!suggestionsBox.contains(e.target) && e.target !== input) {
            suggestionsBox.innerHTML = '';  // Clear suggestions when clicking outside
        }
    });
    function formatDateToText(dateString) {
        if (!dateString) return "Not specified";
        const date = new Date(dateString);
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Intl.DateTimeFormat('en-US', options).format(date);
    }

    function generateLink() {
    // Get the travel date and set it if empty
    let travelDate = document.getElementById('travelDate').value;
    if (!travelDate) {
        // Set to today's date if not filled
        const today = new Date();
        travelDate = today.toISOString().split('T')[0];  // Format as YYYY-MM-DD
        document.getElementById('travelDate').value = travelDate;
    }

    const numPersons = document.getElementById('numPersons').value;
    const pickupLocation = document.getElementById('pickupLocation').value;
    const furtherRequirements = document.getElementById('furtherRequirements').value;

    const selectedDate = travelDate ? formatDateToText(travelDate) : "Not specified";


    let message = '';
    message += `
*Pickup Location* - ${pickupLocation}
*Date :* ${selectedDate}.`;

    if (numPersons && numPersons > 0) {
        message += `
*No of Person -* ${numPersons}`;
    }

    if (furtherRequirements) {
        message += `
*Further Requirement -* ${furtherRequirements}`;
    }

